mapper.select:
SELECT , u.realname as realName, ifnull((select count(s.id) from t_afternoon_tea_select s where s.tea_id=t.id and s.tea_menu_id = #{menuId} and s.tea_task_id = #{taskId}),0) as selectCount, (t.price * ifnull((select count(s.id) from t_afternoon_tea_select s where s.tea_id=t.id and s.tea_menu_id = #{menuId} and s.tea_task_id = #{taskId}),0)) as amount FROM t_afternoon_tea AS t left join t_user u on t.create_user_id = u.id where t.id in
#{id}
ORDER BY t.create_time DESC
