mapper.select:
SELECT , u.realname as realName, a.name as teaName, a.image as teaImage, a.store_address as teaStoreAddress, a.price as teaPrice, concat(m.date,'(',m.week,')') as dateWeek, k.title as taskTitle FROM t_afternoon_tea_select AS t left join t_user u on t.select_user_id = u.id left join t_afternoon_tea a on a.id = t.tea_id left join t_afternoon_tea_menu m on m.id = t.tea_menu_id left join t_afternoon_tea_task k on k.id = t.tea_task_id where 1=1
and t.select_user_id = #{userId}
and t.create_time between #{startDate} and #{endDate}
and a.name like concat('%',#{teaName},'%')
order by ${field} ${sort}
ORDER BY t.create_time DESC

SELECT , u.realname as realName, a.name as teaName, a.image as teaImage, a.store_address as teaStoreAddress, a.price as teaPrice, concat(m.date,'(',m.week,')') as dateWeek, k.title as taskTitle FROM t_afternoon_tea_select AS t left join t_user u on t.select_user_id = u.id left join t_afternoon_tea a on a.id = t.tea_id left join t_afternoon_tea_menu m on m.id = t.tea_menu_id left join t_afternoon_tea_task k on k.id = t.tea_task_id where t.tea_task_id = #{taskId} and t.tea_menu_id = #{menuId}
order by ${field} ${sort}
ORDER BY t.create_time DESC
