mapper.select:
SELECT
    ,
    u.realname as userName
FROM t_attendance AS t
left join t_user u on u.id = t.user_id
where 1=1

    and t.user_id = #{userId}


    and t.create_date between #{startDate} and #{endDate}


    order by ${field} ${sort}


    ORDER BY t.create_time DESC

