mapper.select:
SELECT
    ,
    u.realname as userName
FROM t_attendance_month AS t
left join t_user u on t.user_id = u.id
where 1=1

    and t.user_id = #{userId}


    and t.year = #{year}


    and t.month = #{month}


    order by ${field} ${sort}


    ORDER BY t.month DESC

