mapper.select:
SELECT
    ,
    u.realname
FROM t_file AS t
left join t_user u on t.create_user_id = u.id
where 1=1

    and t.type = #{type}


    and t.create_time between #{startDate} and #{endDate}


    order by ${field} ${sort}


    ORDER BY t.create_time DESC

SELECT , u.realname FROM t_file AS t left join t_user u on t.create_user_id = u.id where t.type = 1
and t.create_user_id = #{userId}
and t.size <= #{fileSize}
and t.width*${height} = ${width}*t.height
and t.width = ${width} and t.height = ${height}
ORDER BY t.create_time DESC
