mapper.select:
SELECT , u.realname as realName, y.name as typeName, p.id as processId, p.status as processStatus, p.process_def_id as processDefId, p.deployment_id as deploymentId, p.process_instance_id as processInstanceId, (select a.receive_user_id from t_leave_process_node a where a.leave_process_id = p.id order by a.deal_time desc limit 1 ) as receiveUserId FROM t_leave AS t left join t_user u on t.user_id = u.id left join t_leave_process p on t.id = p.leave_id left join t_leave_type y on t.type_id = y.id where 1=1
and t.user_id = #{userId}
and t.type_id = #{typeId}
and t.create_time between #{startDate} and #{endDate}
order by ${field} ${sort}
ORDER BY t.create_time DESC

select t.* from ( SELECT , u.realname as realName, y.name as typeName, p.id as processId, p.status as processStatus, p.process_def_id as processDefId, p.deployment_id as deploymentId, p.process_instance_id as processInstanceId, (select a.receive_user_id from t_leave_process_node a where a.leave_process_id = p.id order by a.deal_time desc limit 1 ) as receiveUserId FROM t_leave AS t left join t_user u on t.user_id = u.id left join t_leave_process p on t.id = p.leave_id left join t_leave_type y on t.type_id = y.id where 1=1
and t.id = #{id}
and t.user_id = #{userId}
and t.type_id = #{typeId}
and t.create_time between #{startDate} and #{endDate}
order by ${field} ${sort}
ORDER BY t.create_time DESC
) t where t.processId is not null
