mapper.select:
SELECT

FROM t_log_permission_error AS t
where 1=1

    and t.user_id = #{userId}


    and t.create_time between #{startDate} and #{endDate}


    and t.request_path like concat('%',#{requestPath},'%')


    and t.type = #{type}


    order by ${field} ${sort}


    ORDER BY t.create_time DESC
