mapper.select:
SELECT , u.realname as realName, p.id as processId, p.status as processStatus, p.process_def_id as processDefId, p.deployment_id as deploymentId, p.process_instance_id as processInstanceId FROM t_weekreport AS t left join t_user u on t.user_id = u.id left join t_weekreport_process p on t.id = p.weekreport_id where 1=1
and t.user_id = #{userId}
and t.year = #{year}
and t.start_date like concat('____',#{month},'__') and t.end_date like concat('____',#{year},'__')
order by ${field} ${sort}
ORDER BY t.create_time DESC

select t.* from ( SELECT , u.realname as realName, p.id as processId, p.status as processStatus, p.process_def_id as processDefId, p.deployment_id as deploymentId, p.process_instance_id as processInstanceId FROM t_weekreport AS t left join t_user u on t.user_id = u.id left join t_weekreport_process p on t.id = p.weekreport_id where 1=1
and t.user_id = #{userId}
and t.year = #{year}
and t.start_date like concat('%',#{year},'%') and t.end_date like concat('%',#{year},'%')
order by ${field} ${sort}
ORDER BY t.create_time DESC
) t where t.processId is not null
